import {formatISO9075} from 'date-fns';

export default function Post({title, summary, cover, createdAt, author})
{
    return(
        <div className="post">
        <div className="image">
          <img src="https://techcrunch.com/wp-content/uploads/2023/03/Sonos-Era-100-Era-300-5.jpg?w=940&h=465&crop=1" alt="Sonos Era" />
        </div>
        <div className="content">
          <h2>{title}</h2>
          <p className="info">
            <span className="author">Adithyakrishnan</span>
            <time>{formatISO9075(new Date(createdAt))}</time>
          </p>
          <p>
            {summary}
          </p>
        </div>
      </div>
    );
}
