import {createContext} from 'react';

export const userContext = createContext({});

export function UserContextProvider({children})
{
    const [userInfo, setUserInfo] = useState({});

    return(
        <UserContextProvider value={{userInfo,setUserInfo}}>
            {children}
        </UserContextProvider>
    );
}
